  async removeMasterData() {
    try {
      let directoryPath = path.join(__dirname, "../../public/master");
      fs.readdir(directoryPath, (err, files) => {
        if (err) {
          console.error("Error reading directory:", err);
          return;
        }
        const jsonFiles = files.filter(
          (file) => path.extname(file).toLowerCase() === ".json"
        );

        jsonFiles.forEach(async (jsonFile) => {
          let file = path.join(__dirname, `../../public/master/${jsonFile}`);
          fs.unlink(file, (err) => {
            if (err) {
              console.log(err, "file not removed");
              throw err;
            }
          });
        });
      });
      return;
    } catch (e) {
      console.log(`error in removing master data json`);
      return;
    }
  }
  async addMasterData() {
    try {
      let directoryPath = path.join(__dirname, "../../public/master");
      let schema = {
        masteraccreditations: masterAccreditation,
        masteraddons: masterAddOns,
        masteranscos: masterAnsco,
        masteravailabilities: masterAvailabilities,
        mastercities: masterCities,
        masterexperiences: masterExperience,
        masterhourlyrates: masterHourlyRate,
        masterimmigrationstatuses: masterImmigrationStatus,
        masterindustries: masterIndustry,
        masterjobtitles: masterJobTitles,
        masterlevels: masterLevels,
        masterpromocodes: masterPromoCodes,
        masterqualificationdurations: masterQualificationDuration,
        masterskilllevels: masterSkillLevels,
        masterskills: masterSkills,
        mastersproffessionalbodies: masterProffessionalBody,
        masterstates: masterStates,
        masteryearlyrates: masterYearlyRate,
        masterlicense: masterLicense,
        masterregions: masterRegions,
      };
      fs.readdir(directoryPath, (err, files) => {
        if (err) {
          console.error("Error reading directory:", err);
          return;
        }
        const jsonFiles = files.filter(
          (file) => path.extname(file).toLowerCase() === ".json"
        );

        jsonFiles.forEach(async (jsonFile) => {
          let file = path.join(__dirname, `../../public/master/${jsonFile}`);

          const data = fs.readFileSync(file, "utf8");
          const jsonData = JSON.parse(data);
          let admin = await Admin.findOne({
            emailId: config.superAdminEmail || "seed_admin@grr.la",
          });
          //clean the collection
          await schema[jsonFile.split(".")[0]].deleteMany({});
          for (let json of jsonData) {
            delete json._id;
            delete json.createdAt;
            delete json.updatedAt;
            if (json.expiryDate) {
              json.expiryDate = json.expiryDate["$numberLong"];
            }
            if (json.skillLevel) {
              delete json.skillLevel;
            }
            json.createdBy = admin._id;
            json.modifiedBy = admin._id;

            await schema[jsonFile.split(".")[0]].create(json);
          }
          if (configs.removeMasterData == "true")
            fs.unlink(file, (err) => {
              if (err) {
                console.log(err, "file not removed");
                throw err;
              }
            });
        });
      });
    } catch (e) {
      console.log(e, "err in master seeding");
      return;
    }
  }
