  async addLanguageAndErrorMessage() {
    try {
      let language = {
        language: "English",
        languageCode: "en",
        country: "India",
        isPrimary: true,
      };
      let isKeyExist = await Languages.findOne({
        languageCode: "en",
        isDeleted: false,
      })
        .select({ _id: 1 })
        .exec();
      if (!isKeyExist) {
        await new Model(Languages).store(language);
      }
      let languageId = await Languages.findOne({
        languageCode: "en",
        isDeleted: false,
      })
        .select({ _id: 1 })
        .exec();

      for (const key in LanguageJSON) {
        if (Object.hasOwnProperty.call(LanguageJSON, key)) {
          const value = LanguageJSON[key];
          let errorMessage = {
            messageKey: key,
            message: value,
            languageId: languageId._id,
          };
          let isLanguageMessagesExist = await LanguageMessages.findOne({
            messageKey: key,
            isDeleted: false,
            languageId: languageId._id,
          })
            .select({ _id: 1 })
            .exec();
          if (!isLanguageMessagesExist) {
            await new Model(LanguageMessages).store(errorMessage);
          } else {
            await LanguageMessages.updateOne(
              {
                messageKey: key,
                isDeleted: false,
                languageId: languageId._id,
              },
              { $set: errorMessage }
            );
          }
        }
      }
      return;
    } catch (error) {
      console.log("error", error);
      return;
    }
  }

